		var latitude = lat_min + (Math.random() * lat_range);
		var longitude = lng_min + (Math.random() * lng_range);
		var ret = {
			latitude: latitude,
			longitude: longitude,
			title: 'm' + i
		};
		ret[idKey] = i;
